<?php session_start();require_once ("../conexion/conexion.php");require_once ("funciones.php");$tabla = "rmb_proyecto";$path = "../images/proyecto/";if(isset($_POST['id_sup'])){    $sql_borrar = "DELETE FROM $tabla WHERE ".$tabla."_id = ".$_POST['id_sup']."";    $res_borrar = mysql_query($sql_borrar, conexion());    if($res_borrar){        $nom_id = $_POST['id_sup'];        foreach (scandir($path) as $item) {            if ($item == '.' || $item == '..'){continue;}            else{                $nom_arch = explode(".", $item);                if($nom_arch[0] == $nom_id){unlink($path.$item);}            }               }        echo "Se borro el registro";    }}else if(isset($_POST['id_upd'])){    $campos = "";    $sql_upd = "";    $sq = 0;    foreach($_POST as $key => $value){        if(($key <> 'id_upd') && ($key <> 'rmb_tdoc_id')){            if($sq == 0){$campos .= $key."='".mysql_escape_string($value)."'";}            else{$campos .= ", ".$key."='".mysql_escape_string($value)."'";}            $sq++;        }    }    //comprobamos que sea una petición ajax    if(!empty($_SERVER['HTTP_X_REQUESTED_WITH']) && strtolower($_SERVER['HTTP_X_REQUESTED_WITH']) == 'xmlhttprequest'){        //obtenemos el archivo de la foto a subir.        $file = $_FILES['rmb_proyecto_foto']['name'];        if($file){            $nom_id = $_POST['id_upd']."_foto";            $extension = pathinfo($file, PATHINFO_EXTENSION);            $nom_file = $_POST['id_upd']."_foto".".".$extension;            //comprobamos si existe un directorio para subir el archivo si no es así, lo creamos            if(!is_dir($path)){mkdir($path, 0777);}            //si existe el archivo lo eliminamos            if (file_exists($path.$nom_file)) {unlink($path.$nom_file);}                                     //comprobamos si el archivo ha subido            if ($file && move_uploaded_file($_FILES['rmb_proyecto_foto']['tmp_name'], $path.$nom_file)){                              $campos .= ", rmb_proyecto_foto = '".$path.$nom_file."'";                $docu = "Documento cargado correctamente.\n";            }        }    }    if($tabla == 'rmb_proyecto'){        $campos .= ", rmb_proyecto_tdoc = ".$_POST['rmb_tdoc_id'].", rmb_proyecto_fecha = NOW(), rmb_proyecto_user = ".$_SESSION['UsuID'];    }    $sql_upd = "UPDATE ".$tabla." SET ".$campos." WHERE ".$tabla."_id = '".$_POST['id_upd']."';";    $res_upd = mysql_query($sql_upd, conexion());    if($res_upd){echo $_POST['id_upd'];}    echo $sql_upd;}else{    $nex_id = NextID('rmb_admon', 'rmb_proyecto');    // $nex_id = NextID('eco_empresarial', 'rmb_proyecto');    $sq = 0;$sw = 0;    $campo = "";    $valor = "";    foreach($_POST as $key=>$value){        if(($key <> 'ins') && ($key <> 'rmb_tdoc_id')){            if($sq == 0){$campo .= $key;$valor .= "'".trim($value)."'";}            else{$campo .= ",".$key;$valor .= ",'".trim($value)."'";}                        $sq += 1;        }    }    //obtenemos el archivo a subir    $file = $_FILES['rmb_proyecto_foto']['name'];    if($file){        $nom_id = $nex_id."_foto";        $extension = pathinfo($file, PATHINFO_EXTENSION);        $nom_file = $nom_id.".".$extension;        //comprobamos si existe un directorio para subir el archivo si no es así, lo creamos        if(!is_dir($path)){mkdir($path, 0777);}        //si existe el archivo lo eliminamos        if (file_exists($path.$nom_file)) {unlink($path.$nom_file);}                                 //comprobamos si el archivo ha subido        if ($file && move_uploaded_file($_FILES['rmb_proyecto_foto']['tmp_name'], $path.$nom_file)){                          $campo .= ", rmb_proyecto_foto";            $valor .= ", '".$path.$nom_file."'";            $docu = "Documento cargado correctamente.\n";        }    }    if($tabla == 'rmb_proyecto'){        $campo .= ", rmb_proyecto_tdoc, rmb_proyecto_fecha, rmb_proyecto_user";        $valor .= ", ".$_POST['rmb_tdoc_id'].", NOW(), ".$_SESSION['UsuID'];    }    $sql_ins = "INSERT INTO ".$tabla." (".$campo.") VALUES (".$valor.")";    $res_ins = mysql_query($sql_ins, conexion());    // $used_id = mysql_insert_id(conexion());    if(!$res_ins){$sw += 1;}    if($sw == 0){echo $nex_id;}    else{echo "";}}?>